import React from 'react'
import { useState } from 'react'

export const useField = (type) => {
  const [value, setValue] = useState('')

  const onChange = (event) => {
    setValue(event.target.value)
  }

  const reset = () => {
    setValue('')
  }
  const handleSubmit = (event) => {
    event.preventDefault()
    clearState()
  }

  return {
    type,
    value,
    onChange,
  }
}
